// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.25;

import { COWShedStorage, IMPLEMENTATION_STORAGE_SLOT } from "./COWShedStorage.sol";
import { COWShed, Call } from "./COWShed.sol";
import { Proxy } from "openzeppelin-contracts/contracts/proxy/Proxy.sol";

contract COWShedProxy is COWShedStorage, Proxy {
    error InvalidInitialization();

    event Upgraded(address indexed implementation);

    address internal immutable ADMIN;

    constructor(address implementation, address admin_) {
        ADMIN = admin_;
        assembly {
            sstore(IMPLEMENTATION_STORAGE_SLOT, implementation)
        }
    }

    /// @notice update implementation
    /// @dev will proxy transparently for everyone except the admin.
    function updateImplementation(address newImplementation) external {
        if (msg.sender == ADMIN) {
            assembly {
                sstore(IMPLEMENTATION_STORAGE_SLOT, newImplementation)
            }
            emit Upgraded(newImplementation);
        }
        // transparent proxy for everyone other than admin
        else {
            _fallback();
        }
    }

    /// @notice admin of the proxy.
    /// @dev will proxy transparently for everyone except the proxy itself.
    function admin() external returns (address) {
        if (msg.sender == address(this)) {
            return ADMIN;
        }
        // transparently proxy for everyone other than self. this will allow the implementation
        // to get the admin of the proxy without using the storage, and it costs the same
        // if not less than using admin storage variable
        else {
            _fallback();
        }
    }

    /// @dev revert until the initialization has been completed.
    fallback() external payable override {
        _checkInitialization();
        _fallback();
    }

    receive() external payable {
        _checkInitialization();
        _fallback();
    }

    function _implementation() internal view override returns (address implementation) {
        assembly {
            implementation := sload(IMPLEMENTATION_STORAGE_SLOT)
        }
    }

    function _checkInitialization() internal view {
        if (!_state().initialized && msg.sig != COWShed.initialize.selector) revert InvalidInitialization();
    }
}

src/COWShedResolver.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.25;

import {
    INameResolver, IReverseRegistrar, IENS, IAddrResolver, ENS, ADDR_REVERSE_NODE, sha3HexAddress
} from "./ens.sol";
import { LibString } from "solady/utils/LibString.sol";
import { IERC165 } from "forge-std/interfaces/IERC165.sol";

abstract contract COWShedResolver is INameResolver, IAddrResolver, IERC165 {
    /// @notice maps the `<proxy-address>.<base-name>` node to the user address
    mapping(bytes32 => address) public reverseResolutionNodeToAddress;
    /// @notice maps the subnode label hash(`<user>`) to the proxy address
    mapping(bytes32 => address) public forwardResolutionNodeToAddress;
    /// @dev the base name string expressed as immutable since solidity doesn't support
    ///      immutable strings. E.g. `cowhooks.eth`
    bytes32 immutable baseNameSmallString;
    /// @dev the namehash of the base name. computing at runtime is unnecessary code, so supply
    ///      precomputed. The script should use the `vm.ensNamehash` cheatcode to compute it and
    ///      provide the namehash.
    bytes32 public immutable baseNode;

    constructor(bytes32 bName, bytes32 bNode) {
        baseNameSmallString = bName;
        baseNode = bNode;
    }

    /// @notice Reverse resolution name for given node.
    function name(bytes32 node) external view returns (string memory) {
        address who = reverseResolutionNodeToAddress[node];
        if (who == address(0)) return "";
        return string(abi.encodePacked(LibString.toHexStringChecksummed(who), ".", baseName()));
    }

    /// @notice eth address for the given node.
    function addr(bytes32 node) external view returns (address) {
        address user = forwardResolutionNodeToAddress[node];
        if (user == address(0)) return address(0);
        return user;
    }

    /// @notice baseName/parentName of the factory.
    function baseName() public view returns (string memory) {
        return LibString.fromSmallString(baseNameSmallString);
    }

    function supportsInterface(bytes4 interfaceId) external pure returns (bool) {
        return interfaceId == type(IAddrResolver).interfaceId || interfaceId == type(INameResolver).interfaceId
            || interfaceId == type(IERC165).interfaceId;
    }

    function _setReverseNode(address user, address proxy) internal {
        bytes32 node = keccak256(abi.encodePacked(ADDR_REVERSE_NODE, sha3HexAddress(proxy)));
        reverseResolutionNodeToAddress[node] = user;
    }

    /// @dev support resolving both checksummed and lower case addresses
    function _setForwardNode(address user, address proxy) internal returns (bool) {
        bool success1 = _trySetForwardNodeForAddressString(LibString.toHexStringChecksummed(user), proxy);
        bool success2 = _trySetForwardNodeForAddressString(LibString.toHexString(user), proxy);
        return success1 && success2;
    }

    function _trySetForwardNodeForAddressString(string memory labelString, address proxy)
        internal
        returns (bool success)
    {
        bytes32 label = keccak256(abi.encodePacked(bytes(labelString)));
        bytes32 subnode = keccak256(abi.encodePacked(baseNode, label));
        try ENS.setSubnodeRecord(baseNode, label, address(this), address(this), type(uint64).max) {
            success = true;
            forwardResolutionNodeToAddress[subnode] = proxy;
        } catch (bytes memory) { }
    }
}

src/LibAuthenticatedHooks.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.25;

import { ECDSA } from "solady/utils/ECDSA.sol";
import { Call } from "./ICOWAuthHook.sol";

interface IERC1271 {
    function isValidSignature(bytes32 hash, bytes calldata signature) external view returns (bytes4);
}

library LibAuthenticatedHooks {
    error InvalidSignature();
    error DeadlineElapsed();

    /// @dev EIP712 Call typehash
    bytes32 internal constant CALL_TYPE_HASH =
        keccak256("Call(address target,uint256 value,bytes callData,bool allowFailure,bool isDelegateCall)");
    /// @dev EIP712 ExecuteHooks typehash
    bytes32 internal constant EXECUTE_HOOKS_TYPE_HASH = keccak256(
        "ExecuteHooks(Call[] calls,bytes32 nonce,uint256 deadline)Call(address target,uint256 value,bytes callData,bool allowFailure,bool isDelegateCall)"
    );
    /// @dev magic value that is returned on successful validation of a signature from a EIP1271 smart account.
    bytes4 internal constant MAGIC_VALUE_1271 = 0x1626ba7e;

    /// @dev verifies the deadline of the message and the signature against the executing payload.
    function authenticateHooks(
        Call[] calldata calls,
        bytes32 nonce,
        uint256 deadline,
        address user,
        bytes calldata signature,
        bytes32 domainSeparator
    ) internal view returns (bool) {
        if (block.timestamp > deadline) {
            revert DeadlineElapsed();
        }
        bytes32 toSign = hashToSign(calls, nonce, deadline, domainSeparator);

        // smart contract signer
        if (user.code.length > 0) {
            bool isAuthorized = IERC1271(user).isValidSignature(toSign, signature) == MAGIC_VALUE_1271;
            return (isAuthorized);
        }
        // eoa signer
        else {
            (bytes32 r, bytes32 s, uint8 v) = decodeEOASignature(signature);
            address recovered = ECDSA.recover(toSign, v, r, s);
            return user == recovered;
        }
    }

    /// @dev the EIP712 hash to sign.
    function hashToSign(Call[] calldata calls, bytes32 nonce, uint256 deadline, bytes32 domainSeparator)
        internal
        pure
        returns (bytes32 _toSign)
    {
        bytes32 messageHash = executeHooksMessageHash(calls, nonce, deadline);

        assembly {
            let freeMemoryPointer := mload(0x40)

            mstore(0x00, 0x1901)
            mstore(0x20, domainSeparator)
            mstore(0x40, messageHash)

            _toSign := keccak256(0x1e, 0x42)

            // restore free memory pointer
            mstore(0x40, freeMemoryPointer)
        }
    }

    /// @dev the `hashStruct` encoded hash for the given `ExecuteHooks` message.
    function executeHooksMessageHash(Call[] calldata calls, bytes32 nonce, uint256 deadline)
        internal
        pure
        returns (bytes32 hash)
    {
        bytes32 callshash = callsHash(calls);
        bytes32 executeHooksTypeHash = EXECUTE_HOOKS_TYPE_HASH;

        assembly {
            let before := mload(0x40)
            mstore(0x00, executeHooksTypeHash)
            mstore(0x20, callshash)
            mstore(0x40, nonce)
            mstore(0x60, deadline)
            hash := keccak256(0x00, 0x80)

            // restore free memory pointer
            mstore(0x40, before)
            // restore the zero slot
            mstore(0x60, 0)
        }
    }

    /// @dev the `encodeData` output for the provided calls' dynamic array.
    function callsHash(Call[] calldata calls) internal pure returns (bytes32 _callsHash) {
        uint256 nCalls = calls.length;
        bytes32[] memory hashes = new bytes32[](nCalls);

        for (uint256 i = 0; i < nCalls;) {
            hashes[i] = callHash(calls[i]);
            unchecked {
                ++i;
            }
        }

        assembly ("memory-safe") {
            _callsHash := keccak256(add(hashes, 0x20), mul(nCalls, 0x20))
        }
    }

    /// @dev the `hashStruct` output for given call.
    function callHash(Call calldata cll) internal pure returns (bytes32 _callHash) {
        address target = cll.target;
        uint256 value = cll.value;
        bytes32 callDataHash = keccak256(cll.callData);
        bool allowFailure = cll.allowFailure;
        bool isDelegateCall = cll.isDelegateCall;
        bytes32 callTypeHash = CALL_TYPE_HASH;

        assembly {
            let freeMemoryPointer := mload(0x40)
            let firstSlot := mload(0x80)
            let secondSlot := mload(0xa0)

            mstore(0x00, callTypeHash)
            mstore(0x20, target)
            mstore(0x40, value)
            mstore(0x60, callDataHash)
            mstore(0x80, allowFailure)
            mstore(0xa0, isDelegateCall)
            _callHash := keccak256(0x00, 0xc0)

            // restore free memory pointer
            mstore(0x40, freeMemoryPointer)
            // restore 0 slot
            mstore(0x60, 0x00)
            // restore first slot
            mstore(0x80, firstSlot)
            // restore second slot
            mstore(0xa0, secondSlot)
        }
    }

    /// @dev execute given calls
    function executeCalls(Call[] calldata calls) internal {
        for (uint256 i = 0; i < calls.length;) {
            Call memory call = calls[i];
            bool success;
            bytes memory ret;
            if (call.isDelegateCall) {
                (success, ret) = call.target.delegatecall(call.callData);
            } else {
                (success, ret) = call.target.call{ value: call.value }(call.callData);
            }
            if (!success && !call.allowFailure) {
                // bubble up the revert message
                assembly ("memory-safe") {
                    revert(add(ret, 0x20), mload(ret))
                }
            }

            unchecked {
                ++i;
            }
        }
    }

    /// @dev decodes signatures for EOA as `<r><s><v>` from a packed message.
    function decodeEOASignature(bytes calldata signature) internal pure returns (bytes32 r, bytes32 s, uint8 v) {
        if (signature.length != 65) {
            revert InvalidSignature();
        }
        uint256 mask = 0xff;
        assembly ("memory-safe") {
            r := calldataload(signature.offset)
            s := calldataload(add(signature.offset, 0x20))
            v := and(calldataload(add(signature.offset, 0x21)), mask)
        }
    }
}

src/COWShedStorage.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.25;

import { LibBitmap } from "solady/utils/LibBitmap.sol";

/// @dev bytes32(uint256(keccak256('eip1967.proxy.implementation')) - 1)
bytes32 constant IMPLEMENTATION_STORAGE_SLOT = 0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc;

interface IAdminView {
    function admin() external view returns (address);
}

contract COWShedStorage {
    using LibBitmap for LibBitmap.Bitmap;

    error NonceAlreadyUsed();

    struct State {
        bool initialized;
        address trustedExecutor;
        LibBitmap.Bitmap nonces;
    }

    bytes32 internal constant STATE_STORAGE_SLOT = keccak256("COWShed.State");

    function _state() internal pure returns (State storage state) {
        bytes32 stateSlot = STATE_STORAGE_SLOT;
        assembly {
            state.slot := stateSlot
        }
    }

    function _admin() internal view returns (address) {
        return IAdminView(address(this)).admin();
    }

    function _useNonce(bytes32 _nonce) internal {
        if (_isNonceUsed(_nonce)) revert NonceAlreadyUsed();
        _state().nonces.set(uint256(_nonce));
    }

    function _isNonceUsed(bytes32 _nonce) internal view returns (bool) {
        return _state().nonces.get(uint256(_nonce));
    }
}

